mapper.select:
SELECT tsi.id, tsi.img_name, tsi.ori_img_name, tsi.tourist_spot_id, tsi.image_url, tsi.rep_img_yn, tsi.reg_time, tsi.update_time FROM tourist_spot_images tsi INNER JOIN ( SELECT tourist_spot_id, MIN(id) as min_id FROM tourist_spot_images WHERE rep_img_yn = 'Y' AND tourist_spot_id IN
#{id}
GROUP BY tourist_spot_id ) first_img ON tsi.tourist_spot_id = first_img.tourist_spot_id AND tsi.id = first_img.min_id WHERE tsi.rep_img_yn = 'Y' ORDER BY tsi.tourist_spot_id

SELECT id, img_name, ori_img_name, tourist_spot_id, image_url, rep_img_yn, reg_time, update_time
FROM tourist_spot_images
WHERE id = #{id}

mapper.update:
UPDATE tourist_spot_images
SET rep_img_yn = 'N'
WHERE tourist_spot_id = #{spotId}

UPDATE tourist_spot_images
SET rep_img_yn = 'Y'
WHERE id = #{id}
