INSERT INTO reviews (user_id, tourist_spot_id, title, content, rating, is_approved, created_at, updated_at) VALUES
(2, 1, '일출이 정말 아름다워요!', '바다 위에 세워진 사찰이라 정말 신비로웠어요. 특히 일출 시간에 가면 환상적인 경치를 볼 수 있습니다. 다만 새벽에 가야 해서 조금 힘들긴 하지만 그만한 가치가 있어요!', 5, TRUE, '2025-01-15 08:30:00', '2025-01-15 08:30:00'),
(3, 2, '서핑하기 좋은 곳', '파도가 적당해서 서핑 초보자도 즐길 수 있어요. 해변도 깨끗하고 주변에 맛집도 많아서 하루 종일 놀기 좋습니다.', 4, TRUE, '2025-01-14 14:20:00', '2025-01-14 14:20:00'),
(4, 3, '사진 찍기 좋은 곳!', '색색깔 집들이 정말 예뻐요. 인스타 사진 찍기 완전 좋습니다. 다만 주말에는 사람이 너무 많아서 평일에 가는 걸 추천해요.', 4, TRUE, '2025-01-13 16:45:00', '2025-01-13 16:45:00'),
(5, 5, '힐링되는 카페', '산속 뷰가 정말 좋아요. 커피도 맛있고 분위기도 조용해서 휴식하기 딱 좋습니다. 날씨 좋은 날 드라이브하면서 가기 추천!', 5, TRUE, '2025-01-12 11:15:00', '2025-01-12 11:15:00'),
(6, 1, '관광지로는 좋지만...', '유명한 곳이라 한 번 가볼 만해요. 하지만 사람이 너무 많고 상업적인 느낌이 좀 있어서 조용한 사찰을 기대했다면 아쉬울 수 있어요.', 3, TRUE, '2025-01-11 09:30:00', '2025-01-11 09:30:00'),
(7, 2, '지역민이 추천하는 해수욕장', '해운대보다 덜 복잡하고 현지인들이 많이 가는 곳이에요. 서핑 배우기에도 좋고 주변 먹거리도 다양해서 자주 가는 곳입니다.', 5, TRUE, '2025-01-10 13:20:00', '2025-01-10 13:20:00'),
(8, 4, '아이들과 가기 좋아요', '물이 깨끗하고 파도가 세지 않아서 아이들과 함께 가기 좋습니다. 주차장도 넓어서 편리해요. 가족 단위 방문객들이 많더라구요.', 4, TRUE, '2025-01-09 10:45:00', '2025-01-09 10:45:00'),
(2, 6, '범어사는 정말 평화로워요', '천년고찰의 위엄이 느껴지는 곳이에요. 산책하기도 좋고 명상하기에도 좋은 곳입니다. 가족과 함께 가기 좋아요.', 5, TRUE, '2025-01-08 15:30:00', '2025-01-08 15:30:00'),
(3, 7, '케이블카 전망이 최고!', '부산 시내를 한눈에 볼 수 있어서 정말 좋았어요. 날씨가 좋을 때 가면 더욱 아름다운 경치를 볼 수 있습니다.', 4, TRUE, '2025-01-07 12:10:00', '2025-01-07 12:10:00'),
(4, 8, '해운대는 역시 부산의 대표!', '모래사장이 넓고 깨끗해서 정말 좋았어요. 여름에 가면 더욱 좋을 것 같습니다. 주변에 먹거리도 많아서 좋아요.', 5, TRUE, '2025-01-06 17:25:00', '2025-01-06 17:25:00'),
(5, 9, 'BIFF 광장의 분위기', '영화의 거리답게 문화적인 분위기가 정말 좋았어요. 주변에 카페와 맛집도 많아서 산책하기 좋습니다.', 4, TRUE, '2025-01-05 14:40:00', '2025-01-05 14:40:00'),
(6, 10, '센텀시티 쇼핑의 천국', '정말 큰 쇼핑몰이에요. 하루 종일 있어도 다 못 돌아볼 정도로 넓어요. 브랜드도 다양하고 먹거리도 많아서 좋습니다.', 4, TRUE, '2025-01-04 16:20:00', '2025-01-04 16:20:00'),
(7, 11, '스파랜드에서 힐링', '찜질방이 정말 다양하고 재미있어요. 온도별로 방이 나뉘어져 있어서 원하는 온도에서 휴식을 취할 수 있어요.', 5, TRUE, '2025-01-03 11:50:00', '2025-01-03 11:50:00'),
(8, 12, '아쿠아리움 체험', '해양생물들을 가까이서 볼 수 있어서 정말 신기했어요. 아이들이 특히 좋아할 것 같습니다.', 4, TRUE, '2025-01-02 13:35:00', '2025-01-02 13:35:00'),
(2, 13, '청사포 전망대', '바다 전망이 정말 아름다워요. 일출이나 일몰 시간에 가면 더욱 좋을 것 같습니다.', 4, TRUE, '2025-01-01 18:15:00', '2025-01-01 18:15:00'),
(3, 14, '동래온천 힐링', '천연 온천수라서 정말 좋았어요. 피로가 확실히 풀렸습니다. 가족과 함께 가기 좋은 곳이에요.', 5, TRUE, '2024-12-31 10:25:00', '2024-12-31 10:25:00'),
(4, 15, '동래읍성 역사 체험', '조선시대의 역사를 체험할 수 있어서 정말 좋았어요. 전통 문화를 배울 수 있는 좋은 기회였습니다.', 4, TRUE, '2024-12-30 15:40:00', '2024-12-30 15:40:00'),
(5, 16, '허심청 돔형 온천', '유리천장이 정말 아름다워요. 채광도 좋고 분위기도 좋아서 힐링하기 딱 좋습니다.', 4, TRUE, '2024-12-29 12:30:00', '2024-12-29 12:30:00'),
(6, 17, '구포시장 로컬푸드', '신선한 농산물과 다양한 먹거리를 저렴한 가격에 즐길 수 있어서 좋았어요. 현지인들이 많이 가는 곳이에요.', 4, TRUE, '2024-12-28 09:45:00', '2024-12-28 09:45:00'),
(7, 18, '만덕고개 야경', '야경이 정말 아름다워요. 데이트 코스로도 좋을 것 같습니다. 조용하고 로맨틱한 분위기입니다.', 5, TRUE, '2024-12-27 19:20:00', '2024-12-27 19:20:00'),
(8, 19, '김해공항 교통편', '공항이 정말 깔끔하고 편리해요. 부산 여행의 시작점으로 좋습니다.', 4, TRUE, '2024-12-26 08:10:00', '2024-12-26 08:10:00'),
(2, 20, '강서습지생태공원', '자연을 가까이서 느낄 수 있어서 정말 좋았어요. 철새도 볼 수 있고 산책하기도 좋습니다.', 4, TRUE, '2024-12-25 14:55:00', '2024-12-25 14:55:00'),
(3, 21, '다대포 분수쇼', '일몰과 함께하는 분수쇼가 정말 아름다워요. 로맨틱한 분위기로 데이트하기 좋습니다.', 5, TRUE, '2024-12-24 17:30:00', '2024-12-24 17:30:00'),
(4, 22, '삼락생태공원 자전거', '자전거 도로가 잘 조성되어 있어서 자전거 타기 좋아요. 가족과 함께 나들이하기 좋은 곳입니다.', 4, TRUE, '2024-12-23 11:15:00', '2024-12-23 11:15:00'),
(5, 23, '대천천 누리길', '자연과 함께하는 산책로로 정말 힐링됩니다. 조용하고 평화로운 분위기입니다.', 4, TRUE, '2024-12-22 16:25:00', '2024-12-22 16:25:00'),
(6, 24, '서면 지하상가 쇼핑', '정말 큰 지하상가에요. 패션, 액세서리, 먹거리 등 다양한 상점이 있어서 쇼핑하기 좋습니다.', 4, TRUE, '2024-12-21 13:40:00', '2024-12-21 13:40:00'),
(7, 25, '부평깡통시장 야시장', '야시장 분위기가 정말 좋아요. 젊은 사람들이 많이 가는 곳이고 먹거리도 다양합니다.', 4, TRUE, '2024-12-20 20:15:00', '2024-12-20 20:15:00'),
(8, 26, '전포카페거리', '다양한 카페와 맛집이 있어서 좋아요. 젊은이들의 핫플레이스답게 트렌디한 분위기입니다.', 4, TRUE, '2024-12-19 15:50:00', '2024-12-19 15:50:00'),
(2, 27, 'UN기념공원 평화', '평화와 희생을 되새길 수 있는 의미 있는 장소입니다. 교육적인 가치가 높은 곳이에요.', 4, TRUE, '2024-12-18 10:30:00', '2024-12-18 10:30:00'),
(3, 28, '이기대공원 절경', '해안 절벽을 따라 조성된 공원으로 바다 전망이 정말 아름다워요. 산책하기 좋습니다.', 5, TRUE, '2024-12-17 14:20:00', '2024-12-17 14:20:00'),
(4, 29, '오륙도 섬 투어', '여러 섬들을 한눈에 볼 수 있어서 정말 좋았어요. 스카이워크도 체험할 수 있어서 재미있었습니다.', 4, TRUE, '2024-12-16 11:45:00', '2024-12-16 11:45:00'),
(5, 30, '아미르공원 휴식', '시민들의 휴식공간으로 정말 좋은 곳이에요. 조용하고 평화로운 분위기입니다.', 4, TRUE, '2024-12-15 16:10:00', '2024-12-15 16:10:00'),
(6, 31, '다대포해수욕장 축제', '부산바다축제가 열리는 곳이에요. 여름에 가면 더욱 좋을 것 같습니다.', 4, TRUE, '2024-12-14 13:25:00', '2024-12-14 13:25:00'),
(7, 32, '몰운대 일몰', '일몰 감상하기 정말 좋은 곳이에요. 절경이 정말 아름답습니다.', 5, TRUE, '2024-12-13 17:40:00', '2024-12-13 17:40:00'),
(8, 33, '동구 전망대 야경', '부산항과 시내를 한눈에 볼 수 있어서 정말 좋았어요. 야경이 특히 아름답습니다.', 4, TRUE, '2024-12-12 19:15:00', '2024-12-12 19:15:00'),
(2, 34, '송도해수욕장 역사', '부산 최초의 해수욕장이라서 역사적인 의미가 있어요. 구름다리도 체험할 수 있어서 좋습니다.', 4, TRUE, '2024-12-11 12:30:00', '2024-12-11 12:30:00'),
(3, 35, '감천문화마을 컬러풀', '알록달록한 집들이 정말 예뻐요. 사진 찍기 좋고 인스타그램 명소로 유명합니다.', 5, TRUE, '2024-12-10 15:20:00', '2024-12-10 15:20:00'),
(4, 36, '송도해상케이블카', '바다 위를 지나는 케이블카라서 정말 신기해요. 탁트인 바다 경치가 아름답습니다.', 4, TRUE, '2024-12-09 11:50:00', '2024-12-09 11:50:00'),
(5, 37, '자갈치시장 해산물', '신선한 해산물을 맛볼 수 있어서 정말 좋았어요. 회, 구이, 찜 등 다양한 요리를 즐길 수 있습니다.', 4, TRUE, '2024-12-08 14:35:00', '2024-12-08 14:35:00'),
(6, 38, '국제시장 부산특미', '부산의 전통시장으로 씨앗호떡, 비빔당면 등 부산 특미를 맛볼 수 있어서 좋았어요.', 4, TRUE, '2024-12-07 16:45:00', '2024-12-07 16:45:00'),
(7, 39, '용두산공원 부산타워', '부산타워에서 시내 전경을 한눈에 볼 수 있어서 정말 좋았어요. 야경도 아름답습니다.', 4, TRUE, '2024-12-06 18:20:00', '2024-12-06 18:20:00'),
(8, 40, '온천천 시민공원', '도심 속 자연을 느낄 수 있어서 좋았어요. 산책로와 자전거 도로가 잘 조성되어 있습니다.', 4, TRUE, '2024-12-05 10:15:00', '2024-12-05 10:15:00'),
(2, 41, '연산동 맛집거리', '다양한 음식점과 카페가 밀집되어 있어서 미식가들에게 인기가 높은 곳이에요.', 4, TRUE, '2024-12-04 13:30:00', '2024-12-04 13:30:00'),
(3, 42, '광안리 해수욕장 야경', '광안대교의 야경이 정말 아름다워요. 혼자 여행하기에도 좋은 장소입니다.', 5, TRUE, '2024-12-03 19:25:00', '2024-12-03 19:25:00'),
(4, 43, '광안대교 조명', '밤에 화려한 조명으로 더욱 아름다운 다리입니다. 드라이브하기 좋아요.', 4, TRUE, '2024-12-02 20:10:00', '2024-12-02 20:10:00'),
(5, 44, '태종대 절경', '부산의 대표 관광지로 절경이 정말 아름다워요. 등대와 전망대에서 바다 풍경을 감상할 수 있습니다.', 5, TRUE, '2024-12-01 14:40:00', '2024-12-01 14:40:00'),
(6, 45, '흰여울문화마을 힐링', '바다를 배경으로 한 아름다운 문화마을입니다. 정말 힐링되는 곳이에요.', 4, TRUE, '2024-11-30 12:55:00', '2024-11-30 12:55:00'),
(7, 46, '국립해양박물관', '해양 관련 전시와 체험을 할 수 있어서 정말 좋았어요. 교육적인 가치가 높습니다.', 4, TRUE, '2024-11-29 15:20:00', '2024-11-29 15:20:00');
